arguments.argument:
if (distance_to_point(view_xview + view_wview/2, view_yview + view_hview/2) > 800)
    exit;

var imageOffset;
if(shooting)
{
    shooting=false;
    imageOffset=2;
    if(instance_exists(nearestTarget))
    {
        var hColor, tColor;

        if (ownerPlayer.team == TEAM_BLUE) {
            hColor = make_color_rgb(126, 145, 180);
            tColor = make_color_rgb(201, 220, 255);
        } else {
            hColor = make_color_rgb(216, 79, 89);
            tColor = make_color_rgb(233, 174, 184);
        }

        xshootoffset=4*image_xscale;
        draw_set_alpha(0.6);
        draw_line_width_color(x+lengthdir_x(10,direction)-xshootoffset,y+lengthdir_y(10,direction)-2,nearestTarget.x+random(25)-12,nearestTarget.y+random(25)-12,2,hColor,tColor);
    }
}
else
    imageOffset=0;

if(ownerPlayer.sentry)
{
    if(ownerPlayer.sentry.image_index > 7)
    {
        if(rotating)
            draw_sprite_ext(TurretRotateS,round(rotateindex),x,y,image_xscale,image_yscale,0,c_white,1);
        else
            draw_sprite_ext(sprite_index,ownerPlayer.team + imageOffset,round(x+xoffset*image_xscale),round(y+yoffset),image_xscale,image_yscale,image_angle,c_white,1);
    }
}
