arguments.argument:
if(global.myself.object != -1)
    instance_destroy();

var moved;
moved = false;
stopTracking = false;

//moving view - mapped keys
// only move the view if chat is not open
if !(instance_exists(ChatBox) && global.chatControl) {
    if keyboard_check(global.keyLeft)
    {
        if x > 400 x-=20;
        moved = true;
    }
    else if keyboard_check(global.keyRight)
    {
        if x < map_width()-400 x+=20;
        moved = true;
    }

    if (keyboard_check(global.keyJump) || keyboard_check(global.keyUp))
    {
        if y > 300 y-=20;
        moved = true;
    }
    else if keyboard_check(global.keyDown)
    {
        if y < map_height()-300 y+=20;
        moved = true;
    }
}

if(moved)
{
    afktimer = afktimeout;
    stopTracking = true;
}
else if(tracking)
{
    if(instance_exists(player))
    {
        if(player.team != TEAM_SPECTATOR)
        {
            if(player.object)
            {
                x = round(player.object.x);
                y = round(player.object.y);
            }
        }
        else
            stopTracking = true;
    }
    else
        stopTracking = true;
}

if(tracking and stopTracking) 
{
    tracking = false;
}


// Clip view to map area
x = max(view_wview/2, min(map_width()-view_wview/2, x));
y = max(view_hview/2, min(map_height()-view_hview/2, y));

view_xview = x-view_wview/2;
view_yview = y-view_hview/2;

afktimer-=1;

if((afktimer<=0) and global.isHost==false)
{
    show_message("You have been disconnected from the server. Reason: AFK");
    with(Client)
        instance_destroy();
}
