arguments.argument:
{
    // The following global param variables must be set before creation
    weaponType = global.paramWeaponType;    // weapon type
    ammo = global.paramWeaponAmmo;          // ammo left in the weapon
    alarm[0] = global.paramWeaponLifetime;  // number of frames before the server despawns the item

    // set the proper weapon drop sprite based on the weapon type
    sprite_index = global.weaponDropSprite[weaponType];
}
