arguments.argument:
if (isShieldRegenerating) {
    shieldHp = min(shieldHp+isShieldRegenerating, maxShieldHp);  
    if (shieldHp == maxShieldHp)
        isShieldRegenerating = false;
}
shieldResistance = minShieldResistance * (shieldHp > 0) + (maxShieldResistance - minShieldResistance) * (shieldHp/maxShieldHp);

if (shieldHp > 0) {
    shieldIndex += 4/30;
    if shieldIndex > 4 shieldIndex = 0;
}
