arguments.argument:
// if the other controller doesn't exist, restore crosshairs
if !instance_exists(TeamSelectController) {
    // restore the crosshairs sprite if the player has a character
    if (global.myself != -1) {
        if (global.myself.object != -1) {
            Cursor.sprite_index = global.myself.object.weapons[global.myself.object.currentWeapon].reticleSprite;
        }
    }
}
