arguments.argument:
//automatically change maps if the server is empty for too long
if (global.isHost and hostTimer > 0 and !KothControlPoint.locked and KothControlPoint.capping == 0 and KothControlPoint.bluePresence == 0 and KothControlPoint.redPresence == 0)
{
    hostTimer -= 1;
    if (hostTimer == 0) global.winners = TEAM_SPECTATOR;
}
//both teams' countdown timers
if !KothControlPoint.locked {
    if KothControlPoint.team == TEAM_RED && redTimer >0 redTimer-=1;
    if KothControlPoint.team == TEAM_BLUE && blueTimer >0 blueTimer-=1;
}
//endgame logic
if (redTimer == 0 and KothControlPoint.team == TEAM_RED and KothControlPoint.capping == 0 and KothControlPoint.bluePresence == 0) global.winners = TEAM_RED;
if (blueTimer == 0 and KothControlPoint.team == TEAM_BLUE and KothControlPoint.capping == 0 and KothControlPoint.redPresence == 0) global.winners = TEAM_BLUE;
if global.isHost and KothControlPoint.locked and !global.mapchanging {
    if cpUnlock == 0 {
        sendEventUnlockCP();
        doEventUnlockCP();
    }
    else if cpUnlock == 150 {
        GameServer.syncTimer = 1;
    }
}
if cpUnlock > 0 cpUnlock -= 1;
