arguments.argument:
{
    var g1, g2, s;

    g1 = make_color_rgb(51, 51, 51);
    g2 = make_color_rgb(102, 102, 102);

    // draw the gradient
    draw_rectangle_color(0, 0, 400, 300, g1, g1, g2, g2, false);
    // draw the sprite based on function
    if (global.sumochiSyncFunc == SUMOSYNC_LOAD) s = AchievementsLoadingS;
    if (global.sumochiSyncFunc == SUMOSYNC_SAVE) s = AchievementsSavingS;
    draw_sprite_ext(s, animStep / 7.5, 200, 150, 1, 1, 0, c_white, 1);
}
