arguments.argument:
{
    // set the defaults for the grenade
    armed = false;      // the grenade is armed once it hits a surface or enemy character
    fuseLength = 30;    // the number of frames it takes for the grenade to explode once armed
    damageRadius = 60;
    damageMax = 150;
    damageMin = 60;

    weaponType = WEAPON_FRAGGRENADE;

    bounceCoefficient = 0.5;    // frags are bouncy!

    // number of frames without a collision
    airtime = 0;

    // sounds
    bounceSound = global.FragGrenadeBounceSnd;
    armedSound = -1;
}
