arguments.argument:
{
    if(exploded == true) {
        exit;
    } else {
        exploded = true;
    }
    instance_create(x,y,Explosion);
    playsound(x,y,global.ExplosionSnd);

    with (Character) {
        if (distance_to_object(other) < other.blastRadius and !(team == other.team and id != other.ownerPlayer.object and place_meeting(x, y+1, Obstacle)))
        {
            var rdir, vectorfactor;
            rdir = point_direction(other.x,other.y,x,y);
            vectorfactor = point_distance(0, 0, sin(degtorad(rdir)), cos(degtorad(rdir)));
            if (1 - distance_to_object(other)/other.blastRadius <= 0.25)
                continue;
            motion_add(rdir, min(15, other.knockback-other.knockback*(distance_to_object(other)/other.blastRadius)) * vectorfactor);
            if((other.team != team or id==other.ownerPlayer.object) and !ubered and hp > 0)
            {
                var dmg;
                dmg = other.explosionDamage*(1-(distance_to_object(other)/other.blastRadius));
                if id == other.ownerPlayer.object// self damage resistance
                    dmg *= 5/9;
                hp -= dmg;
                timeUnscathed = 0;
                if (id == other.ownerPlayer.object and lastDamageDealer != -1 and lastDamageDealer != other.ownerPlayer and other.reflector == noone)
                    lastDamageSource = FINISHED_OFF_GIB;
                else 
                {
                    if (lastDamageDealer != other.ownerPlayer and lastDamageDealer != player and other.reflector != lastDamageDealer)
                    {
                        secondToLastDamageDealer = lastDamageDealer;
                        alarm[4] = alarm[3]
                    }
                    if (other.ownerPlayer != id or (other.reflector != noone and other.ownerPlayer == id))
                        alarm[3] = ASSIST_TIME;
                    lastDamageDealer = other.ownerPlayer;
                    lastDamageSource = other.weapon;
                    if (id==other.ownerPlayer.object and other.reflector != noone)
                    {
                        lastDamageDealer = other.reflector;
                        lastDamageSource = WEAPON_REFLECTED_STICKY;
                    }
                }
                if(global.gibLevel > 0)
                {
                    repeat(3)
                    {
                        var blood;
                        blood = instance_create(x,y,Blood);
                        blood.direction = point_direction(other.x,other.y,x,y)-180;
                    }
                }
                if (id==other.ownerPlayer.object)
                {
                    moveStatus = 1;
                    vspeed*=0.8;
                }
                else if (other.team == team)
                {
                    moveStatus = 2;
                    vspeed*=0.8;
                }
                else
                {
                    moveStatus = 4;
                    vspeed*=0.8
                }
            }
            cloakAlpha = max(cloakAlpha, 0.3);
            cloakFlicker = true;
            alarm[7] = 3;
        }
    }


    with (Sentry){
        if (distance_to_object(other) < other.blastRadius) && (team != other.team) { 
        hp -= other.explosionDamage*10/9*(1-(distance_to_object(other)/other.blastRadius)); // we want sentries to take ~11% extra damage so that two stickies will do 100 instead of 90 dmg. we don't change the base damage so scouts can't be 2-hitted.
        lastDamageDealer = other.ownerPlayer;
        lastDamageSource = WEAPON_MINEGUN;
        }   
    }    

     with (Generator){
        if (distance_to_object(other) < other.blastRadius) && (team != other.team) { 
            alarm[0] = regenerationBuffer;
            isShieldRegenerating = false;
            //allow overkill to be applied directly to the target
            var hitDamage;
            hitDamage = other.explosionDamage*(1-(distance_to_object(other)/other.blastRadius));
            if (hitDamage > shieldHp) {
                hp -= hitDamage - shieldHp;
                hp -= shieldHp * shieldResistance;
                shieldHp = 0;
            } else {
                hp -= hitDamage * shieldResistance;
                shieldHp -= hitDamage;
            }
        }   
    }

    with (Rocket) {
        if (distance_to_object(other) < other.blastRadius/4){
            if(distance_to_object(other) < other.blastRadius/7 and (other.team != team or other.ownerPlayer == ownerPlayer)) {
                characterHit=-1;
                event_user(5);
            } else {
                motion_add(point_direction(other.x,other.y,x,y),10-10*(distance_to_object(other)/(other.blastRadius*5)));
            }
        }
    }

    with (DeadGuy) {
        if (distance_to_object(other) < other.blastRadius){
            motion_add(point_direction(other.x,other.y,x,y),10-10*(distance_to_object(other)/other.blastRadius));
        }
    }

    with(Mine) {
        if (distance_to_object(other) < other.blastRadius*0.66 and (other.team != team or other.ownerPlayer == ownerPlayer)){
            event_user(2);
        }
    }
    with (Gib) {
        if (distance_to_object(other) < other.blastRadius){
            motion_add(point_direction(other.x,other.y,x,y),15-15*(distance_to_object(other)/other.blastRadius));
            rotspeed=random(151)-75;
        }
    }

    with(LooseSheet) {
        if (distance_to_object(other) < other.blastRadius){
            motion_add(point_direction(other.x,other.y,x,y),10-10*(distance_to_object(other)/other.blastRadius));
        }
    }
    with(Bubble) {
        if (distance_to_object(other) < other.blastRadius*0.66){
        alarm[0]-=200;
        }    
    }

    instance_destroy();
}
