arguments.argument:
if (other.team != team) {
    other.alarm[0] = other.regenerationBuffer;
    other.isShieldRegenerating = false;
    if (other.shieldHp > 0)
    {
        other.shieldHp -= other.maxShieldHp/3;
        if other.shieldHp < 0 { other.shieldHp = 0; }
    }
    else
    {
        other.hp -= 72;
    }


    instance_destroy();
}
