arguments.argument:
{
    // make sure the collision is not with the owner or a teammate
    if (other.id != owner) && (!onSameTeam(ownerPlayer, other.player)) {
        // common damage
        weaponDealDamage(other.id, shieldDamage, damage, damageBleeds, headshotShield, headshotKill, ownerPlayer, weaponType);
        // destroy the instance
        instance_destroy();
    }
}
