arguments.argument:
{
    // make sure the collision is not with the owner or teammate
    if (other.owner != owner) && (!onSameTeam(ownerPlayer, other.ownerPlayer)) && !stuck {
        // common damage
        weaponDealDamage(other.id, shieldDamage, damage, damageBleeds, headshotShield, headshotKill);
        // destroy the instance
        instance_destroy();
    }
}
