arguments.argument:
{
    if(readyToShoot and ammoCount >= 2.2)
    {
        var shot;
        randomize();

        shot = instance_create(x+lengthdir_x(20,owner.aimDirection),y+12+lengthdir_y(20,owner.aimDirection),Shot);
        shot.direction=owner.aimDirection+(random(14)-7);
        shot.speed=12+random(1);
        shot.owner=owner;
        shot.ownerPlayer=ownerPlayer;
        shot.team=owner.team;
        shot.weapon=WEAPON_MINIGUN;
        shot.hspeed += owner.hspeed;
        shot.alarm[0] = 30;

        if (owner.team == TEAM_RED) {
            playsound(x, y, global.ChaingunSnd);
        } else {
            // play BLU sound and change the shot sprite
            playsound(x, y, global.BlueChaingunSnd);
            shot.sprite_index = BlueChaingunShotS;
        }

        justShot=true;
        readyToShoot=false;
        isRefilling = false;
        ammoCount -= 2.2;
        alarm[0] = refireTime;
        alarm[5] = reloadBuffer;
    }

}
