arguments.argument:
ammoCount = max(0, ammoCount-1);
if (owner.team == TEAM_RED) {
    playsound(x, y, global.BattleRifleSnd);
} else {
    playsound(x,y,global.PhaserRifleSnd);
}
var shot;
repeat(5) {
    shot = instance_create(x,y,Shot);
    shot.hitDamage = 7;
    shot.direction=owner.aimDirection+(random(11)-5);
    shot.speed=11+random(4);
    shot.owner=owner;
    shot.ownerPlayer=ownerPlayer;
    shot.team=owner.team;
    shot.weapon=WEAPON_SHOTGUN;
    //if(golden)
        //shot.sprite_index = ShotGoldS;
    with(shot) {
        speed += owner.hspeed*hspeed/15;
    }
}
justShot=true;
readyToShoot=false;
alarm[0]=refireTime;
alarm[5] = reloadBuffer + reloadTime;

// reset the reload sound effect counter and stop the effect if it is playing
if (ownerPlayer == global.myself) {
    alarm[10] = 65; // originally 40, delayed for 3rd ammo
    FMODInstanceStop(reloadInstance);
}
