arguments.argument:
if (readyToShoot && (ammoCount > 0) && global.isHost) {
    var seed, i;
    seed = irandom(65535);

    // figure out which of the owner's weapons this is
    for (i = 0; i < 3; i += 1) {
        if (owner.weapons[i] == id) break;
    }

    sendEventFireWeapon(ownerPlayer, seed, i);
    doEventFireWeapon(ownerPlayer, seed, i);
}

// Event unique to the shotgun, because reload interrupting is allowed here!
// check to see if the weapon is currently reloading and an interrupt has not already been registered
if (alarm[4] > 0) && !reloadInterrupt {
    // if it is, register the interrupt
    reloadInterrupt = true;
}
